static void
layout_input(char *text, int len)
{
#ifdef HSP_DEBUG
    fprintf(stderr, "Scanning \"%s\"\n", text);
#endif

    hsplineno = hslineno;
    hspcolno = hscolno;

    while (len-- > 0) {
	switch (*text++) {
	case '\n':
	case '\r':
	case '\f':
	    hslineno++;
	    hscolno = 0;
	    break;
	case '\t':
	    hscolno += 8 - (hscolno % 8);	/* Tabs stops are 8 columns apart */
	    break;
	case '\v':
	    break;
	default:
	    ++hscolno;
	    break;
	}
    }
}

